/sc local playerName = "muppet9010"; local biterName = "%%USERNAME%%"; local biterNameColor = {1.0,1.0,1.0,1.0}; local biterType = "behemoth-biter"; local followRange=5; local wonderMaxRange=10; local combatMaxRange=50;
