local biter = surface.create_entity({name=biterType, position=biterSpawnPosition, force=player.force}); if biter == nil then; return; end; if biterName ~= nil and biterName ~= "" then; rendering.draw_text({text=biterName, surface=surface, target=biter, color=biterNameColor}); else; biterName = ""; end; biter.ai_settings.allow_destroy_when_commands_fail = false; biter.ai_settings.allow_try_return_to_spawner = false; biter.ai_settings.do_separation = true;
local followPlayerFunc = function(data);
    data = data --[[@as BiterPet_Data]]
    if not data.biter.valid then; if data.biterName ~= "" then; data.player.print("RIP " .. data.biterName .. ", they were a loyal dumb beast to the end" .. (data.debug and " - " .. game.tick or "")); end; return; end;
