        end;
    elseif biterPlayerDistance < data.wonderMaxRange and data.calledBack then;
        if data.biterName ~= "" then; data.player.print(data.biterName .. " who's a good biter then ... yes you are" .. (data.debug and " - " .. game.tick or "")); end; data.calledBack = false;
    end;
    remote.call("muppet_streamer", "add_delayed_lua", 60, data.followPlayerFuncDump, data); return;
end;
local data = {player=player, biter=biter, followPlayerFuncDump=string.dump(followPlayerFunc), followRange=followRange, wonderMaxRange=wonderMaxRange, combatMaxRange=combatMaxRange, calledBack=false, biterName=biterName, hasOwner=true, debug = debug };
remote.call("muppet_streamer", "add_delayed_lua", 0, string.dump(followPlayerFunc), data);